// 8 Tailwind templates: 4 with photo, 4 without
// Each template exports a render(html) function that returns printable HTML string

export const templates = {
  1: {
    name: 'Classic (No Photo)',
    render: (data) => baseTemplate({ data, withPhoto: false, accent: 'primary' })
  },
  2: {
    name: 'Classic (Photo)',
    render: (data) => baseTemplate({ data, withPhoto: true, accent: 'primary' })
  },
  3: {
    name: 'Elegant (No Photo)',
    render: (data) => baseTemplate({ data, withPhoto: false, accent: 'secondary' })
  },
  4: {
    name: 'Elegant (Photo)',
    render: (data) => baseTemplate({ data, withPhoto: true, accent: 'secondary' })
  },
  5: {
    name: 'Minimal (No Photo)',
    render: (data) => baseTemplate({ data, withPhoto: false, accent: 'zinc' })
  },
  6: {
    name: 'Minimal (Photo)',
    render: (data) => baseTemplate({ data, withPhoto: true, accent: 'zinc' })
  },
  7: {
    name: 'Modern (No Photo)',
    render: (data) => baseTemplate({ data, withPhoto: false, accent: 'emerald' })
  },
  8: {
    name: 'Modern (Photo)',
    render: (data) => baseTemplate({ data, withPhoto: true, accent: 'emerald' })
  },
};

function baseTemplate({ data, withPhoto, accent }) {
  const accentColor = {
    primary: '#2563eb',
    secondary: '#64748b',
    zinc: '#3f3f46',
    emerald: '#059669',
  }[accent] || '#2563eb';

  const sections = (data.sections || []).map(section => `
    <div class="mb-3">
      <h3 class="text-[11pt] font-semibold uppercase tracking-wide" style="color:${accentColor}">${section.title}</h3>
      <div class="text-[10pt] leading-snug">
        ${Array.isArray(section.items) ? section.items.map(item => `<div class="mb-1">${renderItem(item)}</div>`).join('') : ''}
      </div>
    </div>
  `).join('');

  return `<!DOCTYPE html>
  <html>
    <head>
      <meta charset="utf-8" />
      <style>
        @page { size: A4; margin: 18mm 14mm; }
        * { box-sizing: border-box; }
        body { font-family: Inter, system-ui, -apple-system, Segoe UI, Roboto, Arial; color: #111827; }
        .name { font-size: 20pt; font-weight: 700; }
        .role { font-size: 11pt; color: #374151; }
      </style>
    </head>
    <body>
      <div>
        <div style="display:flex; gap:16px; align-items:center; margin-bottom:8px;">
          ${withPhoto && data.photoUrl ? `<img src="${data.photoUrl}" alt="photo" style="width:64px;height:64px;border-radius:8px;object-fit:cover;"/>` : ''}
          <div>
            <div class="name">${escapeHtml(data.name || '')}</div>
            <div class="role">${escapeHtml(data.title || '')}</div>
          </div>
        </div>
        ${sections}
      </div>
    </body>
  </html>`;
}

function renderItem(item) {
  if (!item) return '';
  if (typeof item === 'string') return escapeHtml(item);
  const title = [item.role || item.title, item.company].filter(Boolean).join(' • ');
  const date = [item.startDate, item.endDate].filter(Boolean).join(' - ');
  const bullets = Array.isArray(item.bullets) ? `<ul style="margin:4px 0 0 16px;">${item.bullets.map(b => `<li>${escapeHtml(b)}</li>`).join('')}</ul>` : '';
  return `
    <div>
      ${title ? `<div style="font-weight:600">${escapeHtml(title)}</div>` : ''}
      ${date ? `<div style="font-size:9pt;color:#6b7280">${escapeHtml(date)}</div>` : ''}
      ${item.description ? `<div>${escapeHtml(item.description)}</div>` : ''}
      ${bullets}
    </div>
  `;
}

function escapeHtml(str) {
  return String(str || '')
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&#039;');
}
