import React from 'react';
import { X } from 'lucide-react';

export default function Modal({ open, onClose, title, children, footer, size = 'md', fullscreen = false }) {
  if (!open) return null;

  const sizeClasses = fullscreen
    ? 'w-full h-full'
    : size === 'lg' ? 'w-full max-w-3xl' : size === 'xl' ? 'w-full max-w-5xl' : 'w-full max-w-md';

  const handleBackdrop = (e) => {
    if (e.target === e.currentTarget) onClose?.();
  };

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50"
      onMouseDown={handleBackdrop}
    >
      <div
        className={`bg-white rounded-lg shadow-xl transform transition-all ${sizeClasses} ${fullscreen ? '' : 'mx-4'}`}
        onMouseDown={(e) => e.stopPropagation()}
      >
        <div className={`flex items-center justify-between px-4 ${fullscreen ? 'py-3 border-b' : 'py-3 border-b'}`}>
          <h3 className="text-lg font-semibold text-gray-900">{title}</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="w-5 h-5" />
          </button>
        </div>
        <div className={`px-4 ${fullscreen ? 'py-0' : 'py-4'}`}>
          {children}
        </div>
        {footer && (
          <div className="px-4 py-3 border-t bg-gray-50 rounded-b-lg flex items-center justify-end gap-2">
            {footer}
          </div>
        )}
      </div>
    </div>
  );
}
