import React, { useEffect, useState } from 'react';
import Modal from './Modal';

export default function SectionEditModal({ open, onClose, section, onSave }) {
  const safeSection = section || { title: '', items: [] };
  const [textValue, setTextValue] = useState('');
  const [skillsValue, setSkillsValue] = useState('');
  const [entries, setEntries] = useState([]);

  const typeKey = (safeSection.title || '').toLowerCase();
  const isPersonal = typeKey === 'personal information';
  const isTextOnly = ['summary', 'objective', 'career objective'].includes(typeKey);
  const isSkills = typeKey.includes('skills');
  const isListItem = !isTextOnly && !isSkills && !isPersonal;

  useEffect(() => {
    const items = Array.isArray(safeSection.items) ? safeSection.items : [];
    if (isPersonal) {
      const obj = items[0] || {};
      setTextValue('');
      setSkillsValue('');
      setEntries([{ 
        role: obj.name || '',
        company: obj.title || '',
        startDate: obj.email || '',
        endDate: obj.phone || '',
        description: obj.location || '',
        bullets: Array.isArray(obj.extras) ? obj.extras.map(e => `${e.label}: ${e.value}`) : []
      }]);
    } else if (isTextOnly) {
      setTextValue(items[0] || '');
      setSkillsValue('');
      setEntries([]);
    } else if (isSkills) {
      setSkillsValue(items.join(', '));
      setTextValue('');
      setEntries([]);
    } else if (isListItem) {
      const mapped = items.length ? items.map(it => ({
        role: it.role || it.title || '',
        company: it.company || '',
        startDate: it.startDate || '',
        endDate: it.endDate || '',
        description: it.description || '',
        bullets: Array.isArray(it.bullets) ? it.bullets : []
      })) : [{ role: '', company: '', startDate: '', endDate: '', description: '', bullets: [] }];
      setEntries(mapped);
      setTextValue('');
      setSkillsValue('');
    }
  }, [open, safeSection.title]);

  const addEntry = () => setEntries([...entries, { role: '', company: '', startDate: '', endDate: '', description: '', bullets: [] }]);
  const removeEntry = (idx) => setEntries(entries.filter((_, i) => i !== idx));
  const updateEntry = (idx, field, value) => {
    const copy = entries.slice();
    copy[idx] = { ...copy[idx], [field]: value };
    setEntries(copy);
  };
  const addBullet = (idx) => {
    const copy = entries.slice();
    copy[idx] = { ...copy[idx], bullets: [...(copy[idx].bullets || []), ''] };
    setEntries(copy);
  };
  const updateBullet = (idx, bIdx, value) => {
    const copy = entries.slice();
    const bullets = copy[idx].bullets.slice();
    bullets[bIdx] = value;
    copy[idx].bullets = bullets;
    setEntries(copy);
  };
  const removeBullet = (idx, bIdx) => {
    const copy = entries.slice();
    const bullets = copy[idx].bullets.slice();
    bullets.splice(bIdx, 1);
    copy[idx].bullets = bullets;
    setEntries(copy);
  };

  const handleSave = () => {
    let newItems = [];
    if (isPersonal) {
      const first = entries[0] || { role:'', company:'', startDate:'', endDate:'', description:'', bullets:[] };
      const extras = (first.bullets || []).map(line => {
        const idx = String(line).indexOf(':');
        if (idx === -1) return { label: 'Field', value: String(line) };
        return { label: String(line).slice(0, idx).trim(), value: String(line).slice(idx+1).trim() };
      });
      newItems = [{ name: first.role, title: first.company, email: first.startDate, phone: first.endDate, location: first.description, extras }];
    } else if (isTextOnly) {
      newItems = [textValue];
    } else if (isSkills) {
      newItems = skillsValue.split(',').map(s => s.trim()).filter(Boolean);
    } else if (isListItem) {
      newItems = entries.map(e => ({
        role: e.role,
        title: e.role, // ensure compatibility
        company: e.company,
        startDate: e.startDate,
        endDate: e.endDate,
        description: e.description,
        bullets: e.bullets.filter(Boolean)
      }));
    }
    onSave({ ...safeSection, items: newItems });
    onClose();
  };

  if (!open) return null;

  return (
    <Modal
      open={open}
      onClose={onClose}
      title={`Edit ${safeSection.title}`}
      size="xl"
      footer={(
        <>
          <button onClick={onClose} className="btn-secondary">Cancel</button>
          <button onClick={handleSave} className="btn-primary">Save</button>
        </>
      )}
    >
      <div className="py-4 space-y-4">
        {isTextOnly && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Text</label>
            <textarea value={textValue} onChange={(e) => setTextValue(e.target.value)} rows={6} className="input-field" placeholder="Enter summary or objective..." />
          </div>
        )}

        {isSkills && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Skills (comma-separated)</label>
            <input type="text" value={skillsValue} onChange={(e) => setSkillsValue(e.target.value)} className="input-field" placeholder="e.g. Java, React, SQL" />
          </div>
        )}

        {isListItem && entries.map((entry, idx) => (
          <div key={idx} className="border rounded-lg p-3 space-y-3">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Role/Title</label>
                <input className="input-field" value={entry.role} onChange={(e) => updateEntry(idx, 'role', e.target.value)} />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Company/Org</label>
                <input className="input-field" value={entry.company} onChange={(e) => updateEntry(idx, 'company', e.target.value)} />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Start Date</label>
                <input className="input-field" value={entry.startDate} onChange={(e) => updateEntry(idx, 'startDate', e.target.value)} />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">End Date</label>
                <input className="input-field" value={entry.endDate} onChange={(e) => updateEntry(idx, 'endDate', e.target.value)} />
              </div>
              <div className="sm:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                <textarea className="input-field" rows={3} value={entry.description} onChange={(e) => updateEntry(idx, 'description', e.target.value)} />
              </div>
            </div>

            <div>
              <div className="flex items-center justify-between mb-2">
                <label className="block text-sm font-medium text-gray-700">Bullet Points</label>
                <button className="btn-secondary" onClick={() => addBullet(idx)}>Add Bullet</button>
              </div>
              <div className="space-y-2">
                {(entry.bullets || []).map((b, bIdx) => (
                  <div key={bIdx} className="flex items-center gap-2">
                    <input className="input-field" value={b} onChange={(e) => updateBullet(idx, bIdx, e.target.value)} placeholder="e.g. Achieved X% improvement ..." />
                    <button className="btn-secondary" onClick={() => removeBullet(idx, bIdx)}>Remove</button>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex justify-end">
              <button className="btn-danger" onClick={() => removeEntry(idx)}>Remove Entry</button>
            </div>
          </div>
        ))}

        {isListItem && (
          <div className="flex justify-end">
            <button className="btn-secondary" onClick={addEntry}>Add Entry</button>
          </div>
        )}
      </div>
    </Modal>
  );
}
