import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { Plus, Trash2, Edit3, GripVertical, FileText } from 'lucide-react';
import Modal from './Modal';
import SectionEditModal from './SectionEditModal';

const defaultSections = [
  { id: 'summary', title: 'Summary', items: [] },
  { id: 'experience', title: 'Experience', items: [] },
  { id: 'education', title: 'Education', items: [] },
  { id: 'projects', title: 'Projects', items: [] },
  { id: 'skills', title: 'Skills', items: [] },
  { id: 'certifications', title: 'Certifications', items: [] },
];

export default function SectionManager({ sections, setSections }) {
  const [isAddOpen, setIsAddOpen] = useState(false);
  const [newSectionName, setNewSectionName] = useState('');
  const [newSectionColumn, setNewSectionColumn] = useState('right');
  const [editingIdx, setEditingIdx] = useState(null);
  const [editingContentIdx, setEditingContentIdx] = useState(null);
  const onDragEnd = (result) => {
    if (!result.destination) return;

    const sourceDroppableId = result.source.droppableId;
    const destinationDroppableId = result.destination.droppableId;
    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;

    if (sourceDroppableId === destinationDroppableId && sourceIndex === destinationIndex) {
      return; // No change
    }

    const items = Array.from(sections);
    const [reordered] = items.splice(sourceIndex, 1);

    // Update column if moving between different droppable areas
    if (sourceDroppableId !== destinationDroppableId) {
      if (destinationDroppableId === 'left-column') {
        reordered.column = 'left';
      } else if (destinationDroppableId === 'right-column') {
        reordered.column = 'right';
      }
    }

    items.splice(destinationIndex, 0, reordered);
    setSections(items);
  };

  const addSection = () => {
    setIsAddOpen(true);
  };
  const confirmAdd = () => {
    const title = (newSectionName || '').trim();
    if (!title) return;
    setSections([ ...sections, { id: `${Date.now()}`, title, column: newSectionColumn, items: [] } ]);
    setNewSectionName('');
    setNewSectionColumn('right');
    setIsAddOpen(false);
  };
  const cancelAdd = () => {
    setNewSectionName('');
    setNewSectionColumn('right');
    setIsAddOpen(false);
  };

  const renameSection = (idx) => {
    setEditingIdx(idx);
  };

  const removeSection = (idx) => {
    const isPI = (sections[idx]?.title || '').toLowerCase() === 'personal information';
    if (isPI) return; // cannot delete Personal Information
    if (!window.confirm('Remove section?')) return;
    const next = sections.slice();
    next.splice(idx, 1);
    setSections(next);
  };

  return (
    <>
    <div className="card">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900">Sections</h3>
        <button onClick={addSection} className="btn-secondary">
          <Plus className="w-4 h-4 mr-2" /> Add Section
        </button>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="sections">
          {(provided) => (
            <ul {...provided.droppableProps} ref={provided.innerRef} className="space-y-2">
              {sections.map((section, index) => (
                <Draggable key={section.id} draggableId={String(section.id)} index={index}>
                  {(provided) => (
                    <li ref={provided.innerRef} {...provided.draggableProps} className="flex items-center justify-between bg-white rounded border px-3 py-2">
                      <div className="flex items-center gap-2">
                        <span {...provided.dragHandleProps} className="text-gray-400 cursor-grab">
                          <GripVertical className="w-4 h-4" />
                        </span>
                        <span className="font-medium">{section.title}</span>
                        <span className={`px-2 py-1 text-xs rounded-full font-medium ${
                          section.column === 'left' 
                            ? 'bg-blue-100 text-blue-800' 
                            : 'bg-green-100 text-green-800'
                        }`}>
                          {section.column === 'left' ? 'Left' : 'Right'}
                        </span>
                      </div>
                      <div className="flex items-center gap-2">
                        <button onClick={() => renameSection(index)} className="text-gray-500 hover:text-gray-700">
                          <Edit3 className="w-4 h-4" />
                        </button>
                        <button onClick={() => setEditingContentIdx(index)} className="text-gray-500 hover:text-gray-700">
                          <FileText className="w-4 h-4" />
                        </button>
                        <button onClick={() => removeSection(index)} disabled={(section.title || '').toLowerCase() === 'personal information'} className={` ${((section.title || '').toLowerCase() === 'personal information') ? 'opacity-40 cursor-not-allowed' : 'text-red-500 hover:text-red-700'}`}>
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </li>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>

    <Modal
      open={isAddOpen}
      onClose={cancelAdd}
      title="Add Section"
      footer={(
        <>
          <button onClick={cancelAdd} className="btn-secondary">Cancel</button>
          <button onClick={confirmAdd} className="btn-primary">Add</button>
        </>
      )}
    >
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Section Name</label>
          <input
            type="text"
            value={newSectionName}
            onChange={(e) => setNewSectionName(e.target.value)}
            placeholder="Enter new section name"
            className="input-field"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">Column Placement</label>
          <div className="space-y-2">
            <label className="flex items-center">
              <input
                type="radio"
                name="column"
                value="left"
                checked={newSectionColumn === 'left'}
                onChange={(e) => setNewSectionColumn(e.target.value)}
                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300"
              />
              <span className="ml-2 text-sm text-gray-700">
                <strong>Left Column</strong>
                <div className="text-xs text-gray-500 ml-0">Typically for skills, certifications, hobbies, and shorter sections</div>
              </span>
            </label>
            <label className="flex items-center">
              <input
                type="radio"
                name="column"
                value="right"
                checked={newSectionColumn === 'right'}
                onChange={(e) => setNewSectionColumn(e.target.value)}
                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300"
              />
              <span className="ml-2 text-sm text-gray-700">
                <strong>Right Column</strong>
                <div className="text-xs text-gray-500 ml-0">Typically for experience, education, projects, and longer sections</div>
              </span>
            </label>
          </div>
        </div>
      </div>
    </Modal>

    <SectionEditModal
      open={editingContentIdx !== null}
      onClose={() => setEditingContentIdx(null)}
      section={editingContentIdx !== null ? sections[editingContentIdx] : null}
      onSave={(updated) => {
        if (editingContentIdx === null) return;
        const next = sections.slice();
        next[editingContentIdx] = updated;
        setSections(next);
        setEditingContentIdx(null);
      }}
    />

    <Modal
      open={editingIdx !== null}
      onClose={() => setEditingIdx(null)}
      title="Rename Section"
      footer={(
        <>
          <button onClick={() => setEditingIdx(null)} className="btn-secondary">Cancel</button>
          <button onClick={() => {
            if (editingIdx === null) return;
            const title = (newSectionName || sections[editingIdx].title).trim();
            if (!title) return;
            const next = sections.slice();
            next[editingIdx] = { ...next[editingIdx], title };
            setSections(next);
            setNewSectionName('');
            setEditingIdx(null);
          }} className="btn-primary">Save</button>
        </>
      )}
    >
      {editingIdx !== null && (
        <div className="py-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">Section Name</label>
          <input
            type="text"
            value={newSectionName || sections[editingIdx].title}
            onChange={(e) => setNewSectionName(e.target.value)}
            placeholder="Enter section name"
            className="input-field"
          />
        </div>
      )}
    </Modal>
    </>
  );
}
